import 'package:flutter/material.dart';import 'package:shop_smart/widgets/title_text_.dart';import '../services/assest_manger.dart';import '../widgets/cart_bottom_checkout.dart';import '../widgets/cart_widget.dart';import '../widgets/empty_bag.dart';class CartScreen extends StatelessWidget {  const CartScreen({super.key});  final bool isEmpty = false;  @override  Widget build(BuildContext context) {    return isEmpty        ? Scaffold(            body: EmptyBag(            imagePath: AssetsManager.shoppingBasket,            title: "Your Cart is empty ",            subTitle:                "Looks Like you didnot add anything \n go ahead and start shopping now ",            buttonText: "Shop Now",          ))        : Scaffold(            appBar: AppBar(              title: const TitlesTextWidget(label: "Cart(5)", fontSize: 20),              leading: Image.asset(AssetsManager.shoppingCart),              actions: const [ Icon(Icons.delete,)],            ),      body: const CartWidget(),      bottomSheet: const CartButtomCheckOut(),          );  }}